import {useState} from "react";
import axios from 'axios';
import './ManagementService.css'

const ManagementService = () => {

    const [searchId, setSearchId] = useState('');
    const [student, setStudent] = useState(null);

    const handleSearch = async () => {
        const response = await axios.get(`/api/students/${searchId}`);
        setStudent(response.data);
    };

    return (
        <div className="student-search">
            <div className="student-search__search-box">
                <label className="student-search__label">
                    Search by ID:
                    <input
                        type="text"
                        value={searchId}
                        onChange={(e) => setSearchId(e.target.value)}
                        className="student-search__input"
                    />
                </label>
                <button onClick={handleSearch} className="student-search__button">
                    Search
                </button>
            </div>

            {student && (
                <div className="student-search__student-details">
                    <p className="student-search__heading">{student.name}</p>
                    <p className="student-search__detail">Age: {student.age}</p>
                    <p className="student-search__detail">Grade: {student.grade}</p>
                </div>
            )}
        </div>
    )
}

export default ManagementService;
